import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import UserManagement from './components/UserManagement';
import ClassManagement from './components/ClassManagement';
import MessagingSystem from './components/MessagingSystem';
import EventsManagement from './components/EventsManagement';
import HomeworkManagement from './components/HomeworkManagement';
import GradesManagement from './components/GradesManagement';
import { login, logout } from './services/api';

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const userData = JSON.parse(localStorage.getItem('user'));
      setUser(userData);
    }
    setLoading(false);
  }, []);

  const handleLogin = async (credentials) => {
    try {
      const response = await login(credentials);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      setUser(response.data.user);
    } catch (error) {
      console.error('Erreur de connexion:', error);
      alert('Erreur de connexion. Vérifiez vos identifiants.');
    }
  };

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Erreur lors de la déconnexion:', error);
    } finally {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setUser(null);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        {user ? (
          <>
            <Navigation user={user} onLogout={handleLogout} />
            <main className="container mx-auto p-4 sm:p-6">
              <Routes>
                <Route path="/users" element={<UserManagement user={user} />} />
                <Route path="/classes" element={<ClassManagement user={user} />} />
                <Route path="/messages" element={<MessagingSystem user={user} />} />
                <Route path="/events" element={<EventsManagement user={user} />} />
                <Route path="/homework" element={<HomeworkManagement user={user} />} />
                <Route path="/grades" element={<GradesManagement user={user} />} />
                <Route path="/" element={<Navigate to="/dashboard" />} />
                <Route path="/dashboard" element={<Dashboard user={user} />} />
              </Routes>
            </main>
          </>
        ) : (
          <LoginPage onLogin={handleLogin} />
        )}
      </div>
    </Router>
  );
};

// Composant Dashboard
const Dashboard = ({ user }) => {
  const getWelcomeMessage = () => {
    const hour = new Date().getHours();
    let greeting = '';

    if (hour < 12) greeting = 'Bonjour';
    else if (hour < 18) greeting = 'Bon après-midi';
    else greeting = 'Bonsoir';

    return `${greeting}, ${user.prenom} ${user.nom}`;
  };

  const getRoleDescription = () => {
    switch (user.role) {
      case 'admin':
        return 'Vous avez accès à toutes les fonctionnalités du système';
      case 'professeur':
        return 'Vous pouvez gérer les devoirs, notes et communiquer avec les parents et élèves';
      case 'parent':
        return 'Vous pouvez consulter les devoirs et notes de vos enfants';
      case 'eleve':
        return 'Vous pouvez consulter vos devoirs et notes';
      default:
        return '';
    }
  };

  return (
    <div className="max-w-7xl mx-auto">
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">
          {getWelcomeMessage()}
        </h1>
        <p className="text-gray-600 mb-4">{getRoleDescription()}</p>
        <div className="flex items-center gap-2">
          <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-primary-100 text-primary-800">
            {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
          </span>
        </div>
      </div>

      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
        {/* Cartes d'action rapide */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Messages</h3>
          <p className="text-gray-600 mb-4">Consultez et envoyez des messages</p>
          <a
            href="/messages"
            className="inline-flex items-center px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
          >
            Voir les messages
          </a>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Événements</h3>
          <p className="text-gray-600 mb-4">Consultez les événements du centre</p>
          <a
            href="/events"
            className="inline-flex items-center px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
          >
            Voir les événements
          </a>
        </div>

        {user.role !== 'eleve' && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Devoirs</h3>
            <p className="text-gray-600 mb-4">Gérez les devoirs</p>
            <a
              href="/homework"
              className="inline-flex items-center px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
            >
              Voir les devoirs
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

// Composant LoginPage
const LoginPage = ({ onLogin }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    onLogin({
      email: formData.get('email'),
      password: formData.get('password')
    });
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-primary-50 to-primary-100">
      <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-md">
        <div className="text-center mb-8">
