import axios from 'axios';

const api = axios.create({
  baseURL: 'http://127.0.0.1:8000/api', // aligné avec php artisan serve
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
  },
});

// Intercepteur pour ajouter le token si disponible
api.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
}, error => Promise.reject(error));

export const login = (credentials) => api.post('/auth/login', credentials);
export const logout = () => api.post('/auth/logout');
export const getUsers = () => api.get('/users');
export const saveUser = (data) => api.post('/users', data);
export const deleteUser = (id) => api.delete(`/users/${id}`);
export const getClasses = () => api.get('/classes');
export const saveClass = (data) => api.post('/classes', data);
export const deleteClass = (id) => api.delete(`/classes/${id}`);

// Messages endpoints
export const getMessages = () => api.get('/messages');
export const getConversations = () => api.get('/messages/conversations');
export const getAvailableUsers = () => api.get('/messages/available-users');
export const sendMessage = (data) => api.post('/messages', data);
export const getConversationMessages = (id) => api.get(`/messages/${id}`);

// Events endpoints
export const getEvents = () => api.get('/events');
export const createEvent = (data) => api.post('/events', data);
export const getEvent = (id) => api.get(`/events/${id}`);
export const updateEvent = (id, data) => api.put(`/events/${id}`, data);
export const deleteEvent = (id) => api.delete(`/events/${id}`);

// Homework endpoints
export const getHomework = () => api.get('/homework');
export const createHomework = (data) => api.post('/homework', data);
export const getHomeworkById = (id) => api.get(`/homework/${id}`);
export const updateHomework = (id, data) => api.put(`/homework/${id}`, data);
export const deleteHomework = (id) => api.delete(`/homework/${id}`);

// Grades endpoints
export const getGrades = () => api.get('/grades');
export const createGrade = (data) => api.post('/grades', data);
export const getGradeById = (id) => api.get(`/grades/${id}`);
export const updateGrade = (id, data) => api.put(`/grades/${id}`, data);
export const deleteGrade = (id) => api.delete(`/grades/${id}`);

export default api;
